    private void calcularButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcularButtonActionPerformed
       // declaração de variáveis
       double totalPagar = 0;
       double valorOpcionais = 0;
       double precoBase = 0;
       String totalPagarFormatado = null;
       
       // criando objeto DecimalFormat
       DecimalFormat formato = new DecimalFormat("R$ #,##0.00");
       
       // verificando se o usuário digitou o modelo do carro
       if (modeloCarroTextField.getText().trim().isEmpty()) {
           JOptionPane.showMessageDialog(null, 
                   "Digite o modelo do carro!", "Atenção", 
                   JOptionPane.WARNING_MESSAGE);
           modeloCarroTextField.requestFocus();
           return;
       }
       
       try {
           // obtendo o valor do preço-base
           precoBase = Double.parseDouble(precoBaseTextField.getText());
           
           // verificando os opcionais selecionados
           if (alarmeCheckBox.isSelected()) {
               valorOpcionais += 150.00;
           }
           
           if (mp3CheckBox.isSelected()) {
               valorOpcionais += 100.00;
           }
           
           if (gpsCheckBox.isSelected()) {
               valorOpcionais += 250.00;
           }
           
           if (dispositivoAntiSograCheckBox.isSelected()) {
               valorOpcionais += 400.00;
           }
           
           // calculando valor total
           totalPagar = precoBase + valorOpcionais;
           
           // formatando o valor total a pagar para o formato monetário
           totalPagarFormatado = formato.format(totalPagar);
           
           // mostrando o valor total
           totalPagarLabel.setText(totalPagarFormatado);
           
       } catch (Exception e) {
           JOptionPane.showMessageDialog(null, 
                   e, 
                   "Atenção", JOptionPane.ERROR_MESSAGE);
       }
       
       
       
       
       
    }//GEN-LAST:event_calcularButtonActionPerformed

    private void limparButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_limparButtonActionPerformed
       modeloCarroTextField.setText("");
       precoBaseTextField.setText("0");
       totalPagarLabel.setText("0");
       alarmeCheckBox.setSelected(false);
       mp3CheckBox.setSelected(false);
       gpsCheckBox.setSelected(false);
       dispositivoAntiSograCheckBox.setSelected(false);
    }//GEN-LAST:event_limparButtonActionPerformed

    private void sairButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sairButtonActionPerformed
        if (JOptionPane.showConfirmDialog(null,"Deseja Sair? ","Comfirma",
                JOptionPane.YES_NO_OPTION,JOptionPane.QUESTION_MESSAGE)==0) {
            System.exit(0);
            
        }
        
    }//GEN-LAST:event_sairButtonActionPerformed

